{
  "openapi": "3.0.1",
  "info": {
    "title": "Stock Simulation API",
    "description": "API for the stock simulation application.",
    "version": "v1"
  },
  "paths": {
    "/api/PriceHistory/{stockId}": {
      "get": {
        "tags": [
          "Price History"
        ],
        "parameters": [
          {
            "name": "stockId",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "from",
            "in": "query",
            "schema": {
              "type": "integer",
              "format": "int64"
            }
          },
          {
            "name": "to",
            "in": "query",
            "schema": {
              "type": "integer",
              "format": "int64"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/GetPriceHistoryResponseModel"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ErrorModel"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          }
        }
      }
    },
    "/api/Stocks": {
      "get": {
        "tags": [
          "Stocks"
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ListStocksResponseModel"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          },
          "403": {
            "description": "Forbidden"
          }
        }
      },
      "post": {
        "tags": [
          "Stocks"
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/AddStocksRequestModel"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AddStocksResponseModel"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ErrorModel"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          },
          "403": {
            "description": "Forbidden"
          }
        }
      },
      "delete": {
        "tags": [
          "Stocks"
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/DeleteStocksRequestModel"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DeleteStocksResponseModel"
                }
              }
            }
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ErrorModel"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          },
          "403": {
            "description": "Forbidden"
          }
        }
      },
      "patch": {
        "tags": [
          "Stocks"
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/UpdateStocksRequestModel"
              }
            }
          },
          "required": true
        },
        "responses": {
          "200": {
            "description": "Success"
          },
          "400": {
            "description": "Bad Request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ErrorModel"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          },
          "403": {
            "description": "Forbidden"
          }
        }
      }
    },
    "/api/Stocks/summaries": {
      "get": {
        "tags": [
          "Stocks"
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/SummarizeStocksResponseModel"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "AddStockRequestModel": {
        "required": [
          "drift",
          "startingPrice",
          "ticker",
          "volatility"
        ],
        "type": "object",
        "properties": {
          "ticker": {
            "minLength": 1,
            "type": "string",
            "description": "The ticker of the stock. Must be unique.",
            "example": "GME"
          },
          "startingPrice": {
            "type": "number",
            "description": "The starting price of the stock.",
            "format": "float",
            "example": 100
          },
          "volatility": {
            "type": "number",
            "description": "The volatility of the stock.",
            "format": "float",
            "example": 0.05
          },
          "drift": {
            "type": "number",
            "description": "The drift of the stock.",
            "format": "float",
            "example": 0.01
          }
        },
        "additionalProperties": false,
        "description": "Creates a single stock with the given parameters."
      },
      "AddStockResponseModel": {
        "required": [
          "stockId",
          "ticker"
        ],
        "type": "object",
        "properties": {
          "stockId": {
            "minLength": 1,
            "type": "string",
            "description": "The unique identifier of the stock.",
            "readOnly": true,
            "example": "{9c79fb22-b4c2-4076-bc74-450c460287ad}"
          },
          "ticker": {
            "minLength": 1,
            "type": "string",
            "description": "The ticker symbol of the stock.",
            "readOnly": true,
            "example": "GME"
          }
        },
        "additionalProperties": false,
        "description": "Contains information about a stock that was added."
      },
      "AddStocksRequestModel": {
        "required": [
          "errorIfDuplicate",
          "stocks"
        ],
        "type": "object",
        "properties": {
          "stocks": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/AddStockRequestModel"
            },
            "description": "The stocks to create."
          },
          "errorIfDuplicate": {
            "type": "boolean",
            "description": "If true, an error will be thrown if a stock with the same ticker already exists.\r\nOtherwise, the duplicate stock will be ignored.",
            "example": true
          }
        },
        "additionalProperties": false,
        "description": "Creates new stocks with the given parameters."
      },
      "AddStocksResponseModel": {
        "required": [
          "stocks"
        ],
        "type": "object",
        "properties": {
          "stocks": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/AddStockResponseModel"
            },
            "description": "The stocks that were added.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Contains information about stocks that were added."
      },
      "DeleteStocksRequestModel": {
        "required": [
          "errorIfMissing",
          "stockIds"
        ],
        "type": "object",
        "properties": {
          "stockIds": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "The unique identifiers of the stocks to delete."
          },
          "errorIfMissing": {
            "type": "boolean",
            "description": "Whether to return an error if any of the stocks are missing."
          }
        },
        "additionalProperties": false,
        "description": "Represents a request to delete stocks."
      },
      "DeleteStocksResponseModel": {
        "required": [
          "ids"
        ],
        "type": "object",
        "properties": {
          "ids": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "The unique identifiers of the deleted stocks.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Contains information about the deleted stocks."
      },
      "ErrorModel": {
        "required": [
          "message"
        ],
        "type": "object",
        "properties": {
          "message": {
            "minLength": 1,
            "type": "string",
            "description": "The error message.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Contains information about an error."
      },
      "GetPriceHistoryResponseModel": {
        "required": [
          "from",
          "prices",
          "stockId",
          "to"
        ],
        "type": "object",
        "properties": {
          "stockId": {
            "minLength": 1,
            "type": "string",
            "description": "The unique identifier of the stock.",
            "readOnly": true
          },
          "from": {
            "type": "integer",
            "description": "The first simulation step of the data.\r\nCan be higher than the \"From\" parameter of the request.",
            "format": "int64",
            "readOnly": true,
            "example": 0
          },
          "to": {
            "type": "integer",
            "description": "The last simulation step of the data.\r\nCan be lower than the \"To\" parameter of the request.",
            "format": "int64",
            "readOnly": true,
            "example": 100
          },
          "prices": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/PriceHistoryEntryModel"
            },
            "description": "The price history of the stock.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Represents the response to a request to get the price history of a stock."
      },
      "ListStockResponseModel": {
        "required": [
          "drift",
          "price",
          "stockId",
          "ticker",
          "volatility"
        ],
        "type": "object",
        "properties": {
          "stockId": {
            "minLength": 1,
            "type": "string",
            "description": "The unique identifier of the stock.",
            "readOnly": true,
            "example": "{9c79fb22-b4c2-4076-bc74-450c460287ad}"
          },
          "ticker": {
            "minLength": 1,
            "type": "string",
            "description": "The ticker symbol of the stock.",
            "readOnly": true,
            "example": "GME"
          },
          "volatility": {
            "type": "number",
            "description": "The volatility of the stock.",
            "format": "float",
            "readOnly": true,
            "example": 0.05
          },
          "drift": {
            "type": "number",
            "description": "The drift of the stock.",
            "format": "float",
            "readOnly": true,
            "example": 0.01
          },
          "price": {
            "type": "number",
            "description": "The price of the stock.",
            "format": "float",
            "readOnly": true,
            "example": 100
          }
        },
        "additionalProperties": false,
        "description": "Contains information about a stock."
      },
      "ListStocksResponseModel": {
        "required": [
          "stocks"
        ],
        "type": "object",
        "properties": {
          "stocks": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/ListStockResponseModel"
            },
            "description": "Contains information about a stock.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Contains a list of stocks."
      },
      "PriceHistoryEntryModel": {
        "required": [
          "price",
          "step"
        ],
        "type": "object",
        "properties": {
          "step": {
            "type": "integer",
            "description": "The simulation step of the price.",
            "format": "int64",
            "readOnly": true
          },
          "price": {
            "type": "number",
            "description": "The price of the stock at the simulation step.",
            "format": "float",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Represents an entry in the price history of a stock."
      },
      "SummarizeStockResponseModel": {
        "required": [
          "price",
          "stockId",
          "ticker"
        ],
        "type": "object",
        "properties": {
          "stockId": {
            "minLength": 1,
            "type": "string",
            "description": "The unique identifier of the stock.",
            "readOnly": true,
            "example": "{9c79fb22-b4c2-4076-bc74-450c460287ad}"
          },
          "ticker": {
            "minLength": 1,
            "type": "string",
            "description": "The ticker symbol of the stock.",
            "readOnly": true,
            "example": "GME"
          },
          "price": {
            "type": "number",
            "description": "The price of the stock.",
            "format": "float",
            "readOnly": true,
            "example": 100
          }
        },
        "additionalProperties": false,
        "description": "Contains summaries of a stock."
      },
      "SummarizeStocksResponseModel": {
        "required": [
          "stocks"
        ],
        "type": "object",
        "properties": {
          "stocks": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/SummarizeStockResponseModel"
            },
            "description": "Contains summaries of a stock.",
            "readOnly": true
          }
        },
        "additionalProperties": false,
        "description": "Contains a list of stocks."
      },
      "UpdateStockRequestModel": {
        "required": [
          "stockId"
        ],
        "type": "object",
        "properties": {
          "stockId": {
            "minLength": 1,
            "type": "string",
            "description": "The stock id to update"
          },
          "ticker": {
            "type": "string",
            "description": "The updated ticker. If null, the ticker will not be updated.",
            "nullable": true
          },
          "volatility": {
            "type": "number",
            "description": "The updated volatility. If null, the volatility will not be updated.",
            "format": "float",
            "nullable": true
          },
          "drift": {
            "type": "number",
            "description": "The updated drift. If null, the drift will not be updated.",
            "format": "float",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "The stock id to update"
      },
      "UpdateStocksRequestModel": {
        "required": [
          "stocks"
        ],
        "type": "object",
        "properties": {
          "stocks": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/UpdateStockRequestModel"
            },
            "description": "The stocks to update"
          }
        },
        "additionalProperties": false,
        "description": "Updates stocks."
      }
    },
    "securitySchemes": {
      "oauth2": {
        "type": "oauth2",
        "flows": {
          "authorizationCode": {
            "authorizationUrl": "https://dev-upqv5rwnfhyvo18d.us.auth0.com/authorize?audience=stock-simulation",
            "tokenUrl": "https://dev-upqv5rwnfhyvo18d.us.auth0.com/oauth/token",
            "scopes": {
              "admin": "admin"
            }
          }
        }
      }
    }
  },
  "security": [
    {
      "oauth2": [
        "admin"
      ]
    }
  ]
}
